import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';

@Component({
  selector: 'app-sign-up',
  templateUrl: './sign-up.component.html',
  styleUrls: ['./sign-up.component.css']
})
export class SignUpComponent implements OnInit{

  // username: string;
  // password: string;

  form : FormGroup;
 submitted = false;
  constructor(private router: Router, private fb: FormBuilder) { }

  ngOnInit(): void {
    this.form = this.fb.group(
      {
        name: ['', [Validators.required]],      
        email: ['', [Validators.required, Validators.pattern("^[a-zA-Z0-9_]+@+[a-zA-Z]+[.]+[a-zA-Z]+$")]],
        mobile :['',[Validators.required, Validators.pattern("^[0-9]{10}$"), Validators.maxLength(10)]],
        dob :['',[Validators.required]],
        password: ['',[Validators.required, Validators.pattern("^.*(?=.{8,})(?=.*\d)(?=.*[a-z])(?=.*[A-Z])(?=.*[!*@#$%^&+=]).*$")]],
        confirmPassword: ['', Validators.required],
        // acceptTerms: [false, Validators.requiredTrue]
      },
      // {
      //   validators: [Validation.match('password', 'confirmPassword')]
      // }
    );
  }

  get name() {
    return this.form.get('name')!;
  }
  get email() {
    return this.form.get('email')!;
  }
  get mobile() {
    return this.form.get('mobile')!;
  }
  get dob() {
    return this.form.get('mobile')!;
  }
  get password() {
    return this.form.get('mobile')!;
  }
  get confirmpassword() {
    return this.form.get('mobile')!;
  }

  onSubmit(){
    this.submitted = true;
    if(this.form.valid){
      this.router.navigate(["login"]);
    }
  }







  login(): void {

    this.router.navigate(["login"]);

  }
}
