import { Component } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'app-login-page',
  templateUrl: './login-page.component.html',
  styleUrls: ['./login-page.component.css']
})
export class LoginPageComponent {

  username: string ='';
  password: string ='';

  constructor(private router: Router) { }

  login() : void{
    if(this.username == 'hello' && this.password == 'admin123'){
      this.router.navigate(["home"]);
     }else {
       alert("Invalid credentials");
     }
  }
}
<form>

    <div class="align">
        <div style="text-align: center;font-size: 25px;" class="align-width">
            <label><b>LOGIN</b></label>
        </div>

        <hr>
        <table>
            <tr>
            <div style="text-align: center;" class="align-width">
                <label ><strong>E-MAIL ID :</strong> </label>
                <input id="email"  type="text" placeholder="example@gmail.com" [(ngModel)]="username" [ngModelOptions]="{standalone: true}" required />
                <p>{{username}}</p>
            </div>

            <div style="text-align: center;" class="align-width">
                <label ><strong>PASSWORD :</strong> </label>
                <input id="password" type="password" placeholder="password" [(ngModel)]="password" [ngModelOptions]="{standalone: true}" required />
                <p>{{password}}</p>
            </div>

            <div style="text-align: center;">
                <button class="btn btn-secondary" type="submit" (click)="login()">SUBMIT</button>
            </div>
            <br>
        </table>
    </div>

</form>
