import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';

@Component({
  selector: 'app-sign-up',
  templateUrl: './sign-up.component.html',
  styleUrls: ['./sign-up.component.css']
})
export class SignUpComponent {

  username: string;
  password: string;
  signForm: FormGroup;

  constructor(private router: Router, private fb: FormBuilder) {

  }

  signup(): void {
    if (this.username == 'hello' && this.password == 'admin123') {
      this.router.navigate(["login"]);
    }
  }
  createForm() {
    this.signForm = this.fb.group({
      name: ['', Validators.required]
    });
  }
  onSubmit(){

  }
}
<div class="col-md-4">
    <form [formGroup]="signform" (ngSubmit)="onSubmit()">
      <div class="form-group">
        <label for="username">USERNAME</label>

        <input id="username" type="email" class="form-control" [(ngModel)]="username" [ngModelOptions]="{standalone: true}" #name="ngModel">

      </div>
      <br>
      <div class="form-group">
        <label>PASSWORD</label>

        <input id="username" type="password" class="form-control" [(ngModel)]="password" [ngModelOptions]="{standalone: true}" required/>
      </div>
      <br>
      <button class="btn btn-primary" (click)="signup()" >Sign-up</button>
      <br>  
    </form>
  </div>
