import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';

@Component({
  selector: 'app-sign-up',
  templateUrl: './sign-up.component.html',
  styleUrls: ['./sign-up.component.css']
})
export class SignUpComponent {

  username: string;
  password: string;
  signForm: FormGroup;

  constructor(private router: Router, private fb: FormBuilder) {

    this.createForm();
  }

  signup(): void {
    if (this.username == 'hello' && this.password == 'admin123') {
      this.router.navigate(["login"]);
    }
  }
  createForm() {
    this.signForm = this.fb.group({
      name: ['', Validators.required]
    });
  }
}
<div class="col-md-4">
    <form #signform="ngForm">
      <div class="form-group">
        <label>USERNAME</label>
        <div *ngIf="signform.controls['username'].invalid && (signform.controls['name'].dirty || signform.controls['name'].touched)" class="alert alert-danger">
            <div *ngIf="signform.controls['name'].errors">Name is required.
              </div>
            </div>
        <input id="username" type="email" class="form-control" [(ngModel)]="username" [ngModelOptions]="{standalone: true}" required>

      </div>
      <br>
      <div class="form-group">
        <label>PASSWORD</label>

        <input id="username" type="password" class="form-control" [(ngModel)]="password" [ngModelOptions]="{standalone: true}" required/>
      </div>
      <br>
      <button class="btn btn-primary" (click)="signup()" >Sign-up</button>
      <br>  
    </form>
  </div>
